mapper.update:
UPDATE dish
SET total_likes = total_likes + 1
WHERE dish_id = #{dishId}

UPDATE dish
SET total_likes = total_likes - 1
WHERE dish_id = #{dishId}

UPDATE dish
SET total_favorites = total_favorites + 1
WHERE dish_id = #{dishId}

UPDATE dish
SET total_favorites = total_favorites - 1
WHERE dish_id = #{dishId}
